import React, { useState, useEffect } from 'react';
import {
  Box,
  Card,
  CardContent,
  Grid,
  Typography,
  MenuItem,
  TextField,
  CircularProgress,
  Alert,
} from '@mui/material';
import {
  BarChart,
  Bar,
  PieChart,
  Pie,
  Cell,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
  HeatMap,
} from 'recharts';
import { startOfWeek, startOfMonth, startOfQuarter, startOfYear, format } from 'date-fns';
import axios from 'axios';

const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884D8'];

const timeRanges = [
  { value: 'week', label: 'This Week' },
  { value: 'month', label: 'This Month' },
  { value: 'quarter', label: 'This Quarter' },
  { value: 'year', label: 'This Year' },
  { value: 'all', label: 'All Time' },
];

function Dashboard() {
  const [timeRange, setTimeRange] = useState('month');
  const [projectData, setProjectData] = useState([]);
  const [userData, setUserData] = useState([]);
  const [clientTypeData, setClientTypeData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const getDateRange = () => {
    const now = new Date();
    let startDate;

    switch (timeRange) {
      case 'week':
        startDate = startOfWeek(now);
        break;
      case 'month':
        startDate = startOfMonth(now);
        break;
      case 'quarter':
        startDate = startOfQuarter(now);
        break;
      case 'year':
        startDate = startOfYear(now);
        break;
      default:
        startDate = null;
    }

    return {
      startDate: startDate ? format(startDate, 'yyyy-MM-dd') : null,
      endDate: format(now, 'yyyy-MM-dd'),
    };
  };

  const fetchData = async () => {
    try {
      setLoading(true);
      setError(null);
      const { startDate, endDate } = getDateRange();
      const dateParams = startDate ? `?startDate=${startDate}&endDate=${endDate}` : '';

      console.log('Fetching analytics data with params:', { startDate, endDate });

      const [projectRes, userRes, clientTypeRes] = await Promise.all([
        axios.get(`/api/analytics/time-by-project${dateParams}`),
        axios.get(`/api/analytics/time-by-user${dateParams}`),
        axios.get(`/api/analytics/time-by-client-type${dateParams}`),
      ]);

      console.log('Project data:', projectRes.data);
      console.log('User data:', userRes.data);
      console.log('Client type data:', clientTypeRes.data);

      setProjectData(projectRes.data);
      setUserData(userRes.data);
      setClientTypeData(clientTypeRes.data);
    } catch (err) {
      console.error('Error fetching analytics:', err);
      setError('Failed to fetch analytics data. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, [timeRange]);

  const formatHours = (hours) => {
    return Math.round(hours * 100) / 100;
  };

  // Prepare data for heatmap
  // userData: [{user_id, user_name, project_name, total_hours, ...}]
  const users = Array.from(new Set(userData.map(d => d.user_name)));
  const projects = Array.from(new Set(userData.map(d => d.project_name)));
  // Build a matrix: rows = projects, cols = users
  const heatMapData = projects.map(project =>
    users.map(user => {
      const found = userData.find(d => d.user_name === user && d.project_name === project);
      return found ? found.total_hours : 0;
    })
  );

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="60vh">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 3 }}>
        <Typography variant="h4">Dashboard</Typography>
        <TextField
          select
          value={timeRange}
          onChange={(e) => setTimeRange(e.target.value)}
          sx={{ minWidth: 200 }}
        >
          {timeRanges.map((option) => (
            <MenuItem key={option.value} value={option.value}>
              {option.label}
            </MenuItem>
          ))}
        </TextField>
      </Box>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      <Grid container spacing={3}>
        {/* Project Hours */}
        <Grid item xs={12} md={6}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Hours by Project
              </Typography>
              <Box sx={{ height: 300 }}>
                {projectData.length > 0 ? (
                  <ResponsiveContainer>
                    <BarChart data={projectData}>
                      <CartesianGrid strokeDasharray="3 3" />
                      <XAxis dataKey="project_name" />
                      <YAxis />
                      <Tooltip formatter={(value) => formatHours(value) + ' hours'} />
                      <Legend />
                      <Bar dataKey="total_hours" fill="#0088FE" name="Hours" />
                    </BarChart>
                  </ResponsiveContainer>
                ) : (
                  <Box display="flex" justifyContent="center" alignItems="center" height="100%">
                    <Typography color="text.secondary">No project data available</Typography>
                  </Box>
                )}
              </Box>
            </CardContent>
          </Card>
        </Grid>

        {/* Client Type Distribution */}
        <Grid item xs={12} md={6}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Internal vs External Hours
              </Typography>
              <Box sx={{ height: 300 }}>
                {clientTypeData.length > 0 ? (
                  <ResponsiveContainer>
                    <PieChart>
                      <Pie
                        data={clientTypeData}
                        dataKey="total_hours"
                        nameKey="client_type"
                        cx="50%"
                        cy="50%"
                        outerRadius={100}
                        label={({ client_type, total_hours }) => 
                          `${client_type}: ${formatHours(total_hours)}h`
                        }
                      >
                        {clientTypeData.map((entry, index) => (
                          <Cell key={entry.client_type} fill={COLORS[index % COLORS.length]} />
                        ))}
                      </Pie>
                      <Tooltip formatter={(value) => formatHours(value) + ' hours'} />
                      <Legend />
                    </PieChart>
                  </ResponsiveContainer>
                ) : (
                  <Box display="flex" justifyContent="center" alignItems="center" height="100%">
                    <Typography color="text.secondary">No client type data available</Typography>
                  </Box>
                )}
              </Box>
            </CardContent>
          </Card>
        </Grid>

        {/* User Hours as Heat Map */}
        <Grid item xs={12}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Hours by User and Project (Heat Map)
              </Typography>
              <Box sx={{ height: 400 }}>
                {userData.length > 0 ? (
                  <ResponsiveContainer width="100%" height="100%">
                    {/* recharts does not have a built-in HeatMap, so we use a custom SVG grid */}
                    <svg width="100%" height="100%" viewBox={`0 0 ${users.length * 60 + 100} ${projects.length * 40 + 100}`}>
                      {/* X Axis (Users) */}
                      {users.map((user, i) => (
                        <text
                          key={user}
                          x={i * 60 + 100}
                          y={40}
                          textAnchor="start"
                          fontSize="14"
                          transform={`rotate(30 ${i * 60 + 100} 40)`}
                        >
                          {user}
                        </text>
                      ))}
                      {/* Y Axis (Projects) */}
                      {projects.map((project, j) => (
                        <text key={project} x={80} y={j * 40 + 80} textAnchor="end" fontSize="14" alignmentBaseline="middle">{project}</text>
                      ))}
                      {/* Heatmap cells */}
                      {heatMapData.map((row, j) =>
                        row.map((value, i) => {
                          // Color scale: 0 = #f0f0f0, max = #1976d2
                          const max = Math.max(...heatMapData.flat());
                          const min = 0;
                          const percent = max === min ? 0 : (value - min) / (max - min);
                          const color = `rgb(${240 - percent * 63}, ${240 - percent * 111}, ${240 - percent * 210})`;
                          return (
                            <g key={i + '-' + j}>
                              <rect
                                x={i * 60 + 100}
                                y={j * 40 + 60}
                                width={55}
                                height={35}
                                fill={color}
                                stroke="#ccc"
                              />
                              <text
                                x={i * 60 + 127.5}
                                y={j * 40 + 80}
                                textAnchor="middle"
                                alignmentBaseline="middle"
                                fontSize="16"
                                fill={value > (max / 2) ? '#fff' : '#333'}
                              >
                                {value > 0 ? formatHours(value) : ''}
                              </text>
                            </g>
                          );
                        })
                      )}
                    </svg>
                  </ResponsiveContainer>
                ) : (
                  <Box display="flex" justifyContent="center" alignItems="center" height="100%">
                    <Typography color="text.secondary">No user/project data available</Typography>
                  </Box>
                )}
              </Box>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Box>
  );
}

export default Dashboard;
