import React from 'react';

const lineStyle = {
  height: 2,
  borderRadius: 1,
  margin: '2px 0',
  width: '100%',
  background: '#ccc',
  transition: 'background 0.2s',
};

export default function DayHourBar({ hours = 0, isWeekend = false }) {
  if (isWeekend) {
    return (
      <div style={{ height: 24, width: 16, background: '#ccc', borderRadius: 4, margin: '0 auto' }} />
    );
  }
  let color = '#1976d2'; // blue
  if (hours > 8) color = '#d32f2f'; // red
  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', height: 24, width: 16, margin: '0 auto' }}>
      {[...Array(8)].map((_, i) => (
        <div
          key={i}
          style={{
            ...lineStyle,
            background: i < Math.min(hours, 8) ? color : '#ccc',
            opacity: i < hours ? 1 : 0.5,
          }}
        />
      ))}
    </div>
  );
}
