import React from 'react';

const lineStyle = {
  height: 6,
  borderRadius: 2,
  margin: '3px 0',
  width: '18px',
  background: '#ccc',
  border: '1px solid green', // for debugging
  transition: 'background 0.2s',
};

export default function DayHourBar({ hours = 0, isWeekend = false }) {
  if (isWeekend) {
    return (
      <div style={{ height: 48, width: 20, background: '#ccc', borderRadius: 4, margin: '0 auto', border: '1px solid red' }} />
    );
  }
  let color = '#1976d2'; // blue
  if (hours > 8) color = '#d32f2f'; // red
  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: 48, width: 20, margin: '0 auto', border: '1px solid red' }}>
      {[...Array(8)].map((_, i) => (
        <div
          key={i}
          style={{
            ...lineStyle,
            background: i < Math.min(hours, 8) ? color : '#ccc',
            opacity: i < hours ? 1 : 0.5,
          }}
        />
      ))}
    </div>
  );
}
