import React, { useState, useEffect } from 'react';
import {
  Box,
  Card,
  CardContent,
  Grid,
  Typography,
  MenuItem,
  TextField,
  CircularProgress,
  Alert,
  Switch,
  FormControlLabel,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  IconButton,
  Collapse,
} from '@mui/material';
import {
  BarChart,
  Bar,
  PieChart,
  Pie,
  Cell,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
  HeatMap,
} from 'recharts';
import { startOfWeek, startOfMonth, startOfQuarter, startOfYear, format } from 'date-fns';
import axios from 'axios';
import AddIcon from '@mui/icons-material/Add';
import RemoveIcon from '@mui/icons-material/Remove';

const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884D8'];

const timeRanges = [
  { value: 'week', label: 'This Week' },
  { value: 'month', label: 'This Month' },
  { value: 'quarter', label: 'This Quarter' },
  { value: 'year', label: 'This Year' },
  { value: 'all', label: 'All Time' },
];

function Dashboard() {
  const [timeRange, setTimeRange] = useState('month');
  const [projectData, setProjectData] = useState([]);
  const [userData, setUserData] = useState([]);
  const [clientTypeData, setClientTypeData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showPercent, setShowPercent] = useState(false);
  const [expandedProjects, setExpandedProjects] = useState([]);
  const [showProjectPercent, setShowProjectPercent] = useState(false);

  const getDateRange = () => {
    const now = new Date();
    let startDate;

    switch (timeRange) {
      case 'week':
        startDate = startOfWeek(now, { weekStartsOn: 1 });
        break;
      case 'month':
        startDate = startOfMonth(now);
        break;
      case 'quarter':
        startDate = startOfQuarter(now);
        break;
      case 'year':
        startDate = startOfYear(now);
        break;
      default:
        startDate = null;
    }

    return {
      startDate: startDate ? format(startDate, 'yyyy-MM-dd') + 'T00:00:00.000Z' : null,
      endDate: format(now, 'yyyy-MM-dd') + 'T23:59:59.999Z',
    };
  };

  const fetchData = async () => {
    try {
      setLoading(true);
      setError(null);
      const { startDate, endDate } = getDateRange();
      const dateParams = startDate ? `?startDate=${startDate}&endDate=${endDate}` : '';

      console.log('Fetching analytics data with params:', { startDate, endDate });

      const [projectRes, userRes, clientTypeRes] = await Promise.all([
        axios.get(`/api/analytics/time-by-project${dateParams}`),
        axios.get(`/api/analytics/time-by-user${dateParams}`),
        axios.get(`/api/analytics/time-by-client-type${dateParams}`),
      ]);

      console.log('Project data:', projectRes.data);
      console.log('User data:', userRes.data);
      console.log('Client type data:', clientTypeRes.data);

      setProjectData(projectRes.data);
      setUserData(userRes.data);
      setClientTypeData(clientTypeRes.data);
    } catch (err) {
      console.error('Error fetching analytics:', err);
      setError('Failed to fetch analytics data. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, [timeRange]);

  const formatHours = (hours) => {
    return Math.round(hours * 100) / 100;
  };

  // Prepare users and projects arrays first so they are available for all calculations
  const users = Array.from(new Set(userData.map(d => d.user_name)));
  const projects = Array.from(new Set(userData.map(d => d.project_name)));

  // Helper: count weekdays between two dates (inclusive)
  function countWeekdays(start, end) {
    let count = 0;
    let current = new Date(start);
    while (current <= end) {
      const day = current.getDay();
      if (day !== 0 && day !== 6) count++;
      current.setDate(current.getDate() + 1);
    }
    return count;
  }

  // Calculate period start/end
  const now = new Date();
  let periodStart, periodEnd;
  switch (timeRange) {
    case 'week':
      periodStart = startOfWeek(now);
      periodEnd = now;
      break;
    case 'month':
      periodStart = startOfMonth(now);
      periodEnd = now;
      break;
    case 'quarter':
      periodStart = startOfQuarter(now);
      periodEnd = now;
      break;
    case 'year':
      periodStart = startOfYear(now);
      periodEnd = now;
      break;
    case 'all':
    default:
      // Use earliest and latest date in userData
      const allDates = userData.map(d => new Date(d.start_time)).filter(Boolean);
      periodStart = allDates.length ? new Date(Math.min(...allDates)) : now;
      periodEnd = allDates.length ? new Date(Math.max(...allDates)) : now;
      break;
  }
  const weekdays = countWeekdays(periodStart, periodEnd);
  const userCapacity = users.map(() => weekdays * 8);
  // Calculate total hours logged per user in the period
  const userLogged = users.map(user =>
    userData.filter(d => d.user_name === user).reduce((sum, d) => sum + d.total_hours, 0)
  );
  const userAvailable = userCapacity.map((cap, i) => Math.max(0, cap - userLogged[i]));

  // Calculate total system hours across all projects
  const totalSystemHours = projectData.reduce((sum, project) => sum + project.total_hours, 0);

  // Build a matrix: rows = projects, cols = users
  const heatMapData = [
    userAvailable, // Capacity row
    userLogged,   // Total load row
    ...projects.map(project =>
      users.map(user => {
        const found = userData.find(d => d.user_name === user && d.project_name === project);
        return found ? found.total_hours : 0;
      })
    )
  ];
  const allProjectsWithCapacity = ['Capacity', 'Total load', ...projects];

  // Set column width for heatmap (wider for wrapped names)
  const colWidth = 100;
  const cellWidth = 95;
  const cellHeight = 35;
  const xOffset = 100;
  const yOffset = 60;
  const heatmapRows = allProjectsWithCapacity.length;
  const heatmapHeight = heatmapRows * cellHeight + yOffset + 40; // 40 for header padding

  const handleExpandClick = (projectName) => {
    setExpandedProjects((prev) =>
      prev.includes(projectName)
        ? prev.filter((name) => name !== projectName)
        : [...prev, projectName]
    );
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="60vh">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 3 }}>
        <Typography variant="h4">Dashboard</Typography>
        <TextField
          select
          value={timeRange}
          onChange={(e) => setTimeRange(e.target.value)}
          sx={{ minWidth: 200 }}
        >
          {timeRanges.map((option) => (
            <MenuItem key={option.value} value={option.value}>
              {option.label}
            </MenuItem>
          ))}
        </TextField>
      </Box>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      <Grid container spacing={3}>
        {/* Project Hours (Table) */}
        <Grid item xs={12} md={6}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Hours by Project
              </Typography>
              <FormControlLabel
                control={
                  <Switch
                    checked={showProjectPercent}
                    onChange={e => setShowProjectPercent(e.target.checked)}
                    color="primary"
                  />
                }
                label={showProjectPercent ? 'Show: % of System Total' : 'Show: Hours'}
                sx={{ mb: 2 }}
              />
              <TableContainer component={Paper} sx={{ boxShadow: 'none' }}>
                <Table size="small">
                  <TableHead>
                    <TableRow>
                      <TableCell sx={{ fontWeight: 600 }}>Project</TableCell>
                      <TableCell sx={{ fontWeight: 600 }}>Total Hours</TableCell>
                      <TableCell sx={{ fontWeight: 600 }}>Load</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {projectData.length > 0 ? (
                      (() => {
                        return projectData.flatMap((project) => {
                          const isExpanded = expandedProjects.includes(project.project_name);
                          // Get user breakdown for this project
                          const usersForProject = userData
                            .filter(d => d.project_name === project.project_name && d.total_hours > 0)
                            .map(d => ({ user: d.user_name, hours: d.total_hours }));
                          const maxUserHours = Math.max(...usersForProject.map(u => u.hours), 1);
                          // For percent mode
                          const percent = totalSystemHours > 0 ? (project.total_hours / totalSystemHours) * 100 : 0;
                          return [
                            <TableRow key={project.project_name}>
                              <TableCell sx={{ fontWeight: 500 }}>
                                <IconButton size="small" onClick={() => handleExpandClick(project.project_name)}>
                                  {isExpanded ? <RemoveIcon /> : <AddIcon />}
                                </IconButton>
                                {project.project_name}
                              </TableCell>
                              <TableCell>
                                {showProjectPercent
                                  ? `${Math.round(percent)}%`
                                  : `${formatHours(project.total_hours)}h`}
                              </TableCell>
                              <TableCell>
                                <Box sx={{ width: '100%', height: 16, bgcolor: '#e3eafc', borderRadius: 2, position: 'relative' }}>
                                  <Box
                                    sx={{
                                      width: `${percent}%`,
                                      height: '100%',
                                      bgcolor: '#1976d2',
                                      borderRadius: 2,
                                      transition: 'width 0.3s',
                                    }}
                                  />
                                </Box>
                              </TableCell>
                            </TableRow>,
                            isExpanded && usersForProject.length > 0 && usersForProject.map((u) => {
                              const userPercent = (u.hours / project.total_hours) * 100;
                              return (
                                <TableRow key={project.project_name + '-' + u.user}>
                                  <TableCell sx={{ pl: 6 }}>{u.user}</TableCell>
                                  <TableCell>
                                    {showProjectPercent
                                      ? `${Math.round(userPercent)}%`
                                      : `${formatHours(u.hours)}h`}
                                  </TableCell>
                                  <TableCell>
                                    <Box sx={{ width: '100%', height: 12, bgcolor: '#e3eafc', borderRadius: 2, position: 'relative' }}>
                                      <Box
                                        sx={{
                                          width: `${userPercent}%`,
                                          height: '100%',
                                          bgcolor: '#1976d2',
                                          borderRadius: 2,
                                          transition: 'width 0.3s',
                                        }}
                                      />
                                    </Box>
                                  </TableCell>
                                </TableRow>
                              );
                            }),
                            isExpanded && usersForProject.length === 0 && (
                              <TableRow key={project.project_name + '-no-users'}>
                                <TableCell colSpan={3} align="center">
                                  <Typography color="text.secondary">No user data for this project</Typography>
                                </TableCell>
                              </TableRow>
                            )
                          ];
                        });
                      })()
                    ) : (
                      <TableRow>
                        <TableCell colSpan={3} align="center">
                          <Typography color="text.secondary">No project data available</Typography>
                        </TableCell>
                      </TableRow>
                    )}
                  </TableBody>
                </Table>
              </TableContainer>
            </CardContent>
          </Card>
        </Grid>

        {/* Client Type Distribution */}
        <Grid item xs={12} md={6}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Internal vs External Hours
              </Typography>
              <Box sx={{ height: 300 }}>
                {clientTypeData.length > 0 ? (
                  <ResponsiveContainer>
                    <PieChart>
                      <Pie
                        data={clientTypeData}
                        dataKey="total_hours"
                        nameKey="client_type"
                        cx="50%"
                        cy="50%"
                        outerRadius={100}
                        label={({ client_type, total_hours }) => 
                          `${client_type}: ${formatHours(total_hours)}h`
                        }
                      >
                        {clientTypeData.map((entry, index) => (
                          <Cell key={entry.client_type} fill={COLORS[index % COLORS.length]} />
                        ))}
                      </Pie>
                      <Tooltip formatter={(value) => formatHours(value) + ' hours'} />
                      <Legend />
                    </PieChart>
                  </ResponsiveContainer>
                ) : (
                  <Box display="flex" justifyContent="center" alignItems="center" height="100%">
                    <Typography color="text.secondary">No client type data available</Typography>
                  </Box>
                )}
              </Box>
            </CardContent>
          </Card>
        </Grid>

        {/* User Hours as Heat Map */}
        <Grid item xs={12}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Hours by User and Project (Heat Map)
              </Typography>
              <FormControlLabel
                control={
                  <Switch
                    checked={showPercent}
                    onChange={e => setShowPercent(e.target.checked)}
                    color="primary"
                  />
                }
                label={showPercent ? 'Show: % of Capacity' : 'Show: Hours'}
                sx={{ mb: 2 }}
              />
              <Box sx={{ height: heatmapHeight }}>
                {userData.length > 0 ? (
                  <ResponsiveContainer width="100%" height={heatmapHeight}>
                    {/* recharts does not have a built-in HeatMap, so we use a custom SVG grid */}
                    <svg width="100%" height="100%" viewBox={`0 0 ${users.length * colWidth + 100} ${heatmapHeight}`}>
                      {/* X Axis (Users) */}
                      {users.map((user, i) => {
                        const words = user.split(' ');
                        const lineHeight = 18;
                        // The last line should be at y=55, so the first line is at y=55 - (words.length-1)*lineHeight
                        const startY = 55 - (words.length - 1) * lineHeight;
                        return (
                          <text
                            key={user}
                            x={i * colWidth + xOffset + cellWidth / 2}
                            y={startY}
                            textAnchor="middle"
                            fontSize="14"
                          >
                            {words.map((word, idx) => (
                              <tspan
                                key={word + idx}
                                x={i * colWidth + xOffset + cellWidth / 2}
                                dy={idx === 0 ? 0 : lineHeight}
                              >
                                {word}
                              </tspan>
                            ))}
                          </text>
                        );
                      })}
                      {/* Y Axis (Projects) */}
                      {allProjectsWithCapacity.map((project, j) => (
                        <text key={project} x={xOffset - 20} y={j * cellHeight + yOffset + cellHeight / 2} textAnchor="end" fontSize="14" alignmentBaseline="middle">{project}</text>
                      ))}
                      {/* Heatmap cells */}
                      {heatMapData.map((row, j) =>
                        row.map((value, i) => {
                          // For percent mode, calculate percent of capacity
                          let displayValue = '';
                          let percentValue = 0;
                          if (showPercent) {
                            if (j === 0) {
                              // Capacity row
                              percentValue = 100;
                            } else {
                              // All other rows
                              percentValue = userCapacity[i] > 0 ? (value / userCapacity[i]) * 100 : 0;
                            }
                            displayValue = percentValue > 0 ? `${Math.round(percentValue)}%` : '';
                          } else {
                            displayValue = value > 0 ? formatHours(value) : '';
                          }
                          // Color scale: 0 = #f0f0f0, max = #1976d2
                          const max = Math.max(...heatMapData.flat());
                          const min = 0;
                          const percent = max === min ? 0 : (value - min) / (max - min);
                          const color = `rgb(${240 - percent * 63}, ${240 - percent * 111}, ${240 - percent * 210})`;
                          return (
                            <g key={i + '-' + j}>
                              <rect
                                x={i * colWidth + xOffset}
                                y={j * cellHeight + yOffset}
                                width={cellWidth}
                                height={cellHeight}
                                fill={color}
                                stroke="#ccc"
                              />
                              <text
                                x={i * colWidth + xOffset + cellWidth / 2}
                                y={j * cellHeight + yOffset + cellHeight / 2}
                                textAnchor="middle"
                                alignmentBaseline="middle"
                                fontSize="16"
                                fill={value > (max / 2) ? '#fff' : '#333'}
                              >
                                {displayValue}
                              </text>
                            </g>
                          );
                        })
                      )}
                    </svg>
                  </ResponsiveContainer>
                ) : (
                  <Box display="flex" justifyContent="center" alignItems="center" height="100%">
                    <Typography color="text.secondary">No user/project data available</Typography>
                  </Box>
                )}
              </Box>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Box>
  );
}

export default Dashboard;
