import React, { useState, useEffect } from 'react';
import {
  Box,
  Button,
  Typography,
  TextField,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  MenuItem,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Alert,
  IconButton
} from '@mui/material';
import { format } from 'date-fns';
import axios from 'axios';
import { Add, Delete, Remove, Edit as EditIcon } from '@mui/icons-material';

const daysOfWeek = [
  { key: 'mon', label: 'Mon' },
  { key: 'tue', label: 'Tue' },
  { key: 'wed', label: 'Wed' },
  { key: 'thu', label: 'Thu' },
  { key: 'fri', label: 'Fri' },
  { key: 'sat', label: 'Sat' },
  { key: 'sun', label: 'Sun' },
];
const requiredHoursPerDay = {
  mon: 8,
  tue: 8,
  wed: 8,
  thu: 8,
  fri: 8,
  sat: 0,
  sun: 0,
};

function getMonday(d) {
  d = new Date(d);
  var day = d.getDay(),
    diff = d.getDate() - day + (day === 0 ? -6 : 1); // adjust when day is sunday
  return new Date(d.setDate(diff));
}

function HourInput({ value, onChange }) {
  const handleDecrement = () => {
    const newValue = Math.max(0, (parseFloat(value) || 0) - 1);
    onChange(newValue === 0 ? '' : newValue);
  };
  const handleIncrement = () => {
    const newValue = Math.min(24, (parseFloat(value) || 0) + 1);
    onChange(newValue);
  };
  return (
    <Box sx={{ display: 'flex', alignItems: 'center', border: '1px solid #e0e0e0', borderRadius: 2, px: 0.5, py: 0.2, minWidth: 48, justifyContent: 'center', background: '#fff' }}>
      <IconButton size="small" onClick={handleDecrement} sx={{ p: 0.25 }}>
        <Remove fontSize="small" />
      </IconButton>
      <Typography sx={{ mx: 0.5, minWidth: 16, textAlign: 'center', fontWeight: 500, fontSize: 14 }}>
        {value || 0}
      </Typography>
      <IconButton size="small" onClick={handleIncrement} sx={{ p: 0.25 }}>
        <Add fontSize="small" />
      </IconButton>
    </Box>
  );
}

function TimeEntries() {
  const [timeEntries, setTimeEntries] = useState([]);
  const [projects, setProjects] = useState([]);
  const [users, setUsers] = useState([]);
  const [open, setOpen] = useState(false);
  const [error, setError] = useState(null);
  const [newEntry, setNewEntry] = useState({
    project_id: '',
    user_id: '',
    start_time: '',
    end_time: '',
    description: '',
  });
  const [weeklyEntries, setWeeklyEntries] = useState([
    {
      project_id: '',
      user_id: '',
      hours: { mon: '', tue: '', wed: '', thu: '', fri: '', sat: '', sun: '' },
    },
  ]);
  const [weekStart, setWeekStart] = useState(getMonday(new Date()));
  const [selectedUser, setSelectedUser] = useState('');
  const [editDialogOpen, setEditDialogOpen] = useState(false);
  const [editEntry, setEditEntry] = useState(null);
  const [editHours, setEditHours] = useState('');
  const [editLoading, setEditLoading] = useState(false);
  const [editError, setEditError] = useState('');

  useEffect(() => {
    fetchTimeEntries();
    fetchProjects();
    fetchUsers();
  }, []);

  useEffect(() => {
    if (users.length > 0 && !selectedUser) {
      setSelectedUser(users[0].id);
      setWeeklyEntries(weeklyEntries.map(entry => ({ ...entry, user_id: users[0].id })));
    }
    // eslint-disable-next-line
  }, [users]);

  const fetchTimeEntries = async () => {
    try {
      const response = await axios.get('/api/time-entries');
      console.log('Fetched time entries:', response.data);
      setTimeEntries(response.data);
    } catch (error) {
      console.error('Error fetching time entries:', error);
      setError('Failed to fetch time entries. Please try again.');
    }
  };

  const fetchProjects = async () => {
    try {
      const response = await axios.get('/api/projects');
      console.log('Fetched projects:', response.data);
      setProjects(response.data);
    } catch (error) {
      console.error('Error fetching projects:', error);
      setError('Failed to fetch projects. Please try again.');
    }
  };

  const fetchUsers = async () => {
    try {
      const response = await axios.get('/api/users');
      console.log('Fetched users:', response.data);
      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching users:', error);
      setError('Failed to fetch users. Please try again.');
    }
  };

  const handleOpen = () => {
    setError(null);
    setOpen(true);
  };

  const handleClose = () => {
    setError(null);
    setOpen(false);
  };

  const handleSubmit = async () => {
    try {
      if (!newEntry.project_id) {
        setError('Please select a project');
        return;
      }

      if (!newEntry.user_id) {
        setError('Please select a user');
        return;
      }

      if (!newEntry.start_time) {
        setError('Start time is required');
        return;
      }

      if (!newEntry.end_time) {
        setError('End time is required');
        return;
      }

      console.log('Submitting new time entry:', newEntry);
      const response = await axios.post('/api/time-entries', newEntry);
      console.log('Time entry created:', response.data);

      fetchTimeEntries();
      handleClose();
      setNewEntry({
        project_id: '',
        user_id: '',
        start_time: '',
        end_time: '',
        description: '',
      });
    } catch (error) {
      console.error('Error creating time entry:', error);
      setError(error.response?.data?.error || 'Failed to create time entry. Please try again.');
    }
  };

  const calculateDuration = (startTime, endTime) => {
    const start = new Date(startTime);
    const end = new Date(endTime);
    const diffInMilliseconds = end - start;
    const hours = Math.floor(diffInMilliseconds / (1000 * 60 * 60));
    const minutes = Math.floor((diffInMilliseconds % (1000 * 60 * 60)) / (1000 * 60));
    return `${hours}h ${minutes}m`;
  };

  const addProjectRow = () => {
    setWeeklyEntries([
      ...weeklyEntries,
      { project_id: '', user_id: selectedUser, hours: { mon: '', tue: '', wed: '', thu: '', fri: '', sat: '', sun: '' } },
    ]);
  };

  const removeProjectRow = (idx) => {
    setWeeklyEntries(weeklyEntries.filter((_, i) => i !== idx));
  };

  const handleWeeklyChange = (idx, field, value) => {
    const updated = [...weeklyEntries];
    updated[idx][field] = value;
    setWeeklyEntries(updated);
  };

  const handleHourChange = (idx, day, value) => {
    const updated = [...weeklyEntries];
    if (typeof value === 'string') {
      updated[idx].hours[day] = value.replace(/[^0-9.]/g, '');
    } else {
      updated[idx].hours[day] = value;
    }
    setWeeklyEntries(updated);
  };

  const dayTotals = daysOfWeek.map(day =>
    weeklyEntries.reduce((sum, entry) => sum + (parseFloat(entry.hours[day.key]) || 0), 0)
  );
  const projectTotals = weeklyEntries.map(entry =>
    daysOfWeek.reduce((sum, day) => sum + (parseFloat(entry.hours[day.key]) || 0), 0)
  );
  const requiredHoursTotal = Object.values(requiredHoursPerDay).reduce((a, b) => a + b, 0);

  const handleWeeklySubmit = async () => {
    setError(null);
    let hasError = false;
    for (const entry of weeklyEntries) {
      if (!entry.project_id || !entry.user_id) {
        setError('Please select a project and user for each row.');
        hasError = true;
        break;
      }
    }
    if (hasError) return;
    const submissionTime = new Date().toISOString();
    const promises = [];
    weeklyEntries.forEach((entry) => {
      daysOfWeek.forEach((day, i) => {
        const hours = parseFloat(entry.hours[day.key]);
        if (hours && hours > 0) {
          const date = new Date(weekStart);
          date.setDate(date.getDate() + i);
          const start_time = new Date(date);
          start_time.setHours(9, 0, 0, 0);
          const end_time = new Date(start_time);
          end_time.setHours(start_time.getHours() + hours);
          promises.push(
            axios.post('/api/time-entries', {
              project_id: entry.project_id,
              user_id: entry.user_id,
              start_time: start_time.toISOString(),
              end_time: end_time.toISOString(),
              description: '',
              submission_time: submissionTime,
            })
          );
        }
      });
    });
    try {
      await Promise.all(promises);
      fetchTimeEntries();
      setWeeklyEntries([
        { project_id: '', user_id: selectedUser, hours: { mon: '', tue: '', wed: '', thu: '', fri: '', sat: '', sun: '' } },
      ]);
    } catch (error) {
      setError('Failed to submit time entries.');
    }
  };

  // Group timeEntries by project_name and submission_time
  const groupedEntries = Object.values(
    timeEntries.reduce((acc, entry) => {
      const key = `${entry.project_name}|${entry.submission_time}`;
      if (!acc[key]) {
        acc[key] = {
          project_name: entry.project_name,
          submission_time: entry.submission_time,
          total_hours: 0,
        };
      }
      acc[key].total_hours += (new Date(entry.end_time) - new Date(entry.start_time)) / (1000 * 60 * 60);
      return acc;
    }, {})
  ).sort((a, b) => new Date(b.submission_time) - new Date(a.submission_time));

  // In the log table, assign numbers so the most recent entry gets the largest number
  groupedEntries.forEach((entry, idx, arr) => entry.displayNumber = arr.length - idx);

  const handleEditOpen = (entry) => {
    setEditEntry(entry);
    setEditHours(entry.total_hours.toFixed(2));
    setEditDialogOpen(true);
    setEditError('');
  };

  const handleEditClose = () => {
    setEditDialogOpen(false);
    setEditEntry(null);
    setEditHours('');
    setEditError('');
  };

  const handleEditSave = async () => {
    setEditLoading(true);
    setEditError('');
    try {
      // Fetch all time_entries for this project and submission_time
      const res = await axios.get('/api/time-entries');
      const entriesToUpdate = res.data.filter(e => e.project_name === editEntry.project_name && e.submission_time === editEntry.submission_time);
      const totalOld = entriesToUpdate.reduce((sum, e) => sum + (new Date(e.end_time) - new Date(e.start_time)) / (1000 * 60 * 60), 0);
      const ratio = parseFloat(editHours) / totalOld;
      // Update each entry's duration proportionally
      await Promise.all(entriesToUpdate.map(e => {
        const oldDuration = (new Date(e.end_time) - new Date(e.start_time)) / (1000 * 60 * 60);
        const newDuration = oldDuration * ratio;
        const newEnd = new Date(e.start_time);
        newEnd.setHours(newEnd.getHours() + newDuration);
        return axios.patch(`/api/time-entries/${e.id}`, {
          end_time: newEnd.toISOString(),
        });
      }));
      setEditDialogOpen(false);
      fetchTimeEntries();
    } catch (err) {
      setEditError('Failed to update entry.');
    } finally {
      setEditLoading(false);
    }
  };

  return (
    <Box>
      <Box sx={{ display: 'flex', alignItems: 'center', mb: 2, gap: 2 }}>
        <Typography variant="h4">Time Entries</Typography>
        <TextField
          select
          size="small"
          label="User"
          value={selectedUser}
          onChange={e => {
            setSelectedUser(e.target.value);
            setWeeklyEntries(weeklyEntries.map(entry => ({ ...entry, user_id: e.target.value })));
          }}
          sx={{ minWidth: 180, ml: 2 }}
        >
          {users.map(user => (
            <MenuItem key={user.id} value={user.id}>{user.name}</MenuItem>
          ))}
        </TextField>
      </Box>
      <TableContainer component={Paper} sx={{ mb: 3 }}>
        <Table size="small">
          <TableHead>
            {/* Weekday names row */}
            <TableRow>
              <TableCell></TableCell>
              {daysOfWeek.map((day) => (
                <TableCell key={day.key} align="center" sx={{ backgroundColor: (day.key === 'sat' || day.key === 'sun') ? '#f5f5f5' : undefined, fontWeight: 'bold' }}>
                  {day.label}
                </TableCell>
              ))}
              <TableCell sx={{ width: 1, minWidth: 1, px: 0.25, py: 0 }}></TableCell>
            </TableRow>
            {/* Date row */}
            <TableRow>
              <TableCell sx={{ width: '100%' }}>Project</TableCell>
              {daysOfWeek.map((day, i) => (
                <TableCell key={day.key} align="center" sx={{ backgroundColor: (day.key === 'sat' || day.key === 'sun') ? '#f5f5f5' : undefined, minWidth: 56, px: 0.5, py: 0.5, fontWeight: 'normal', fontSize: 13 }}>
                  {format(new Date(weekStart.getTime() + i * 86400000), 'dd.MM')}
                </TableCell>
              ))}
              <TableCell align="center">Total</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {weeklyEntries.map((entry, idx) => (
              <TableRow key={idx}>
                <TableCell sx={{ width: '100%' }}>
                  <TextField
                    select
                    size="small"
                    value={entry.project_id}
                    onChange={e => handleWeeklyChange(idx, 'project_id', e.target.value)}
                    fullWidth
                  >
                    {projects.map(project => (
                      <MenuItem key={project.id} value={project.id}>{project.name}</MenuItem>
                    ))}
                  </TextField>
                </TableCell>
                {daysOfWeek.map(day => (
                  <TableCell key={day.key} align="center" sx={{ backgroundColor: (day.key === 'sat' || day.key === 'sun') ? '#f5f5f5' : undefined, minWidth: 56, px: 0.5, py: 0.5 }}>
                    <HourInput
                      value={entry.hours[day.key]}
                      onChange={val => handleHourChange(idx, day.key, val)}
                    />
                  </TableCell>
                ))}
                <TableCell align="center" sx={{ fontWeight: 'bold' }}>{projectTotals[idx]}</TableCell>
                <TableCell sx={{ width: 1, minWidth: 1, px: 0.25, py: 0 }}>
                  <IconButton onClick={() => removeProjectRow(idx)} disabled={weeklyEntries.length === 1} sx={{ p: 0.25 }}>
                    <Delete fontSize="small" />
                  </IconButton>
                </TableCell>
              </TableRow>
            ))}
            <TableRow>
              <TableCell colSpan={daysOfWeek.length + 3} sx={{ border: 'none', p: 1 }}>
                <Button
                  variant="text"
                  color="primary"
                  size="small"
                  startIcon={<Add />}
                  onClick={addProjectRow}
                  sx={{ mt: 1, fontWeight: 'normal', fontSize: 16, textTransform: 'none', pl: 0, minHeight: 'unset', minWidth: 'unset', p: 0 }}
                >
                  Add Project
                </Button>
              </TableCell>
            </TableRow>
            <TableRow>
              <TableCell sx={{ fontWeight: 'bold' }}>Required Hours</TableCell>
              {daysOfWeek.map(day => (
                <TableCell key={day.key} align="center" sx={{ color: 'gray', backgroundColor: (day.key === 'sat' || day.key === 'sun') ? '#f5f5f5' : undefined }}>{requiredHoursPerDay[day.key]}</TableCell>
              ))}
              <TableCell align="center" sx={{ color: 'gray', fontWeight: 'bold' }}>{requiredHoursTotal}</TableCell>
              <TableCell sx={{ width: 1, minWidth: 1, px: 0.25, py: 0 }} />
            </TableRow>
            <TableRow>
              <TableCell sx={{ fontWeight: 'bold' }}>Total</TableCell>
              {dayTotals.map((total, i) => (
                <TableCell key={i} align="center" sx={{ color: total < requiredHoursPerDay[daysOfWeek[i].key] ? 'red' : 'inherit', backgroundColor: (daysOfWeek[i].key === 'sat' || daysOfWeek[i].key === 'sun') ? '#f5f5f5' : undefined }}>{total}</TableCell>
              ))}
              <TableCell align="center" sx={{ fontWeight: 'bold', color: dayTotals.reduce((a, b) => a + b, 0) < requiredHoursTotal ? 'red' : 'inherit' }}>{dayTotals.reduce((a, b) => a + b, 0)}</TableCell>
              <TableCell sx={{ width: 1, minWidth: 1, px: 0.25, py: 0 }} />
            </TableRow>
          </TableBody>
        </Table>
      </TableContainer>
      <Box sx={{ display: 'flex', gap: 2, mb: 3 }}>
        <Button variant="contained" color="primary" onClick={handleWeeklySubmit}>Submit Week</Button>
      </Box>
      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>
      )}
      <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 3 }}>
        <Typography variant="h4">Time Entries</Typography>
        <Button variant="contained" color="primary" onClick={handleOpen}>
          Add Time Entry
        </Button>
      </Box>
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>#</TableCell>
              <TableCell>Project</TableCell>
              <TableCell>Total Hours</TableCell>
              <TableCell>Submission Date/Time</TableCell>
              <TableCell align="right">Edit</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {groupedEntries.map((entry) => (
              <TableRow key={entry.project_name + entry.submission_time}>
                <TableCell>{entry.displayNumber}</TableCell>
                <TableCell>{entry.project_name}</TableCell>
                <TableCell>{entry.total_hours.toFixed(2)}</TableCell>
                <TableCell>{entry.submission_time ? format(new Date(entry.submission_time), 'PPpp') : ''}</TableCell>
                <TableCell align="right">
                  <Button size="small" variant="outlined" onClick={() => handleEditOpen(entry)}>
                    Edit
                  </Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Add Time Entry</DialogTitle>
        <DialogContent>
          <TextField
            select
            fullWidth
            margin="dense"
            label="Project"
            value={newEntry.project_id}
            onChange={(e) => setNewEntry({ ...newEntry, project_id: e.target.value })}
            error={!!error && !newEntry.project_id}
            helperText={!newEntry.project_id ? 'Please select a project' : ''}
          >
            {projects.map((project) => (
              <MenuItem key={project.id} value={project.id}>
                {project.name}
              </MenuItem>
            ))}
          </TextField>
          <TextField
            select
            fullWidth
            margin="dense"
            label="User"
            value={newEntry.user_id}
            onChange={(e) => setNewEntry({ ...newEntry, user_id: e.target.value })}
            error={!!error && !newEntry.user_id}
            helperText={!newEntry.user_id ? 'Please select a user' : ''}
          >
            {users.map((user) => (
              <MenuItem key={user.id} value={user.id}>
                {user.name}
              </MenuItem>
            ))}
          </TextField>
          <TextField
            fullWidth
            margin="dense"
            label="Start Time"
            type="datetime-local"
            value={newEntry.start_time}
            onChange={(e) => setNewEntry({ ...newEntry, start_time: e.target.value })}
            InputLabelProps={{
              shrink: true,
            }}
            error={!!error && !newEntry.start_time}
            helperText={!newEntry.start_time ? 'Start time is required' : ''}
          />
          <TextField
            fullWidth
            margin="dense"
            label="End Time"
            type="datetime-local"
            value={newEntry.end_time}
            onChange={(e) => setNewEntry({ ...newEntry, end_time: e.target.value })}
            InputLabelProps={{
              shrink: true,
            }}
            error={!!error && !newEntry.end_time}
            helperText={!newEntry.end_time ? 'End time is required' : ''}
          />
          <TextField
            fullWidth
            margin="dense"
            label="Description"
            multiline
            rows={4}
            value={newEntry.description}
            onChange={(e) => setNewEntry({ ...newEntry, description: e.target.value })}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button onClick={handleSubmit} variant="contained" color="primary">
            Add Entry
          </Button>
        </DialogActions>
      </Dialog>

      <Dialog open={editDialogOpen} onClose={handleEditClose}>
        <DialogTitle>Edit Total Hours</DialogTitle>
        <DialogContent>
          <TextField
            label="Total Hours"
            type="number"
            value={editHours}
            onChange={e => setEditHours(e.target.value)}
            fullWidth
            margin="dense"
            inputProps={{ min: 0, step: 0.01 }}
          />
          {editError && <Alert severity="error" sx={{ mt: 2 }}>{editError}</Alert>}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleEditClose}>Cancel</Button>
          <Button onClick={handleEditSave} variant="contained" color="primary" disabled={editLoading}>
            Save
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
}

export default TimeEntries;
