import React from 'react';
import { Box, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, TextField, MenuItem, Button, DialogActions, Typography } from '@mui/material';

function SingleProjectWeekEditor({ entry, projects, daysOfWeek, weekStart, onChange, onSave, onCancel, error, loading }) {
  const handleProjectChange = (e) => {
    onChange({ ...entry, project_id: e.target.value });
  };
  const handleHourChange = (dayKey, value) => {
    const hours = { ...entry.hours, [dayKey]: value.replace(/[^0-9.]/g, '') };
    onChange({ ...entry, hours });
  };
  const projectTotals = daysOfWeek.reduce((sum, day) => sum + (parseFloat(entry.hours[day.key]) || 0), 0);

  return (
    <Box>
      <TableContainer component={Paper} sx={{ mb: 2 }}>
        <Table size="small">
          <TableHead>
            <TableRow>
              <TableCell>Project</TableCell>
              {daysOfWeek.map((day, i) => (
                <TableCell key={day.key} align="center">
                  <Typography variant="body2">
                    {day.label}
                  </Typography>
                  <Typography variant="caption" color="textSecondary">
                    {new Date(weekStart.getTime() + i * 86400000).toLocaleDateString()}
                  </Typography>
                </TableCell>
              ))}
              <TableCell align="center">Total</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            <TableRow>
              <TableCell sx={{ width: '100%' }}>
                <TextField
                  select
                  size="small"
                  value={entry.project_id}
                  onChange={handleProjectChange}
                  fullWidth
                >
                  {projects.map(project => (
                    <MenuItem key={project.id} value={project.id}>{project.name}</MenuItem>
                  ))}
                </TextField>
              </TableCell>
              {daysOfWeek.map(day => (
                <TableCell key={day.key} align="center">
                  <TextField
                    size="small"
                    value={entry.hours[day.key] || ''}
                    onChange={e => handleHourChange(day.key, e.target.value)}
                    inputProps={{ min: 0, style: { textAlign: 'center', width: 40 } }}
                  />
                </TableCell>
              ))}
              <TableCell align="center" sx={{ fontWeight: 'bold' }}>{projectTotals}</TableCell>
            </TableRow>
          </TableBody>
        </Table>
      </TableContainer>
      {error && <Typography color="error" sx={{ mb: 2 }}>{error}</Typography>}
      <DialogActions>
        <Button onClick={onCancel}>Cancel</Button>
        <Button onClick={onSave} variant="contained" color="primary" disabled={loading}>
          Save
        </Button>
      </DialogActions>
    </Box>
  );
}

export default SingleProjectWeekEditor;
